// NGRX
import { createFeatureSelector, createSelector } from '@ngrx/store';
export const getCartItemsState = createFeatureSelector('cart');
export const getCompareState = createFeatureSelector('compare');
export const getWishlistState = createFeatureSelector('wishlist');
/************************    CartItem Selectors   ***********************/
export const cartItemsSelector = createSelector(getCartItemsState, cartItemState => {
  return cartItemState.data;
});
/************************    Wishlist Selectors   ***********************/
export const wishlistSelector = createSelector(getWishlistState, wishlistState => {
  return wishlistState.data;
});
/************************    Compare Selectors   ***********************/
export const compareSelector = createSelector(getCompareState, compareState => {
  return compareState.data;
});
